COST_OFFSET = 1600000

# Get cumulative cost to date
cumulative_to_date = billing_df.agg(F.max('CumulativeCost')).collect()[0][0]


adjusted_df = billing_df.alias('adjusted_df')

# Rename columns
for col in adjusted_df.columns:
    if col.startswith("Predicted"):
        adjusted_df = adjusted_df.withColumnRenamed(col, "Adjusted_" + col)

# Iterate over predicted cost columns
for col in adjusted_df.columns:
    if col.startswith("Adjusted_PredictedCost"):
        adjusted_df = adjusted_df.withColumn(col, 
            F.when(F.col(col).isNull(), None)
            .when(F.col('Date') <= '2024-04-01', F.col(col))
            .otherwise(F.col(col) + COST_OFFSET)
        )

# Iterate over predicted cumulative cost columns
for col in adjusted_df.columns:
    if col.startswith("Adjusted_PredictedCumulative"):
        predicted_cost_col = col.replace('Cumulative', '')
        adjusted_df = adjusted_df.withColumn(col, F.sum(predicted_cost_col).over(window) + cumulative_to_date)

