def load_to_df(start_date):
    df = spark.createDataFrame([], schema=schema)
    file_paths = compute_file_paths(start_date)
    for file_path in file_paths:
        try:
            file_df = spark.read.parquet(file_path)
            print(f"Success reading file {file_path}")
            df = df.union(file_df)
        except Exception as e:
            print(f"Error reading file {file_path}: {str(e)}")
    return df

def fetch_subscriptions(access_token):
    api_endpoint = "https://management.azure.com/subscriptions?api-version=2022-12-01"

    headers = {
        'Authorization': 'Bearer ' + access_token
    }

    response = requests.get(api_endpoint, headers=headers)
    result = response.json()
    return result

